import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, FileSpreadsheet, X, CheckCircle, AlertCircle, Plus, Trash2 } from 'lucide-react';
import API from '../utils/api';
import QuickAddContact from './QuickAddContact';

export default function ExcelImport({ onComplete }) {
  const [showOptions, setShowOptions] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [result, setResult] = useState(null);
  const fileInputRef = useRef(null);
  const [imports, setImports] = useState([]);

  const getStoredImports = () => {
    try {
      return JSON.parse(localStorage.getItem('excelImports') || '[]');
    } catch {
      return [];
    }
  };

  const setStoredImports = (list) => {
    localStorage.setItem('excelImports', JSON.stringify(list));
  };

  React.useEffect(() => {
    if (isOpen) {
      setImports(getStoredImports());
    }
  }, [isOpen]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file) uploadFile(file);
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) uploadFile(file);
  };

  const handleAddMore = () => {
    fileInputRef.current?.click();
  };

  const handleDeleteImport = (id) => {
    const updated = getStoredImports().filter(i => i.id !== id);
    setStoredImports(updated);
    setImports(updated);
  };

  const uploadFile = async (file) => {
    if (!file.name.match(/\.(xlsx|xls)$/)) {
      setResult({ error: 'Please select an Excel file (.xlsx or .xls)' });
      return;
    }

    setUploading(true);
    setResult(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const res = await API.post('/contacts/import', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setResult({
        success: true,
        ...res.data
      });

      const newEntry = {
        id: Date.now() + '-' + Math.random().toString(36).slice(2),
        name: file.name,
        uploadedAt: new Date().toISOString(),
        added: res.data?.added || 0,
        skipped: res.data?.skipped || 0
      };
      const updated = [newEntry, ...getStoredImports()];
      setStoredImports(updated);
      setImports(updated);

      setTimeout(() => {
        if (onComplete) onComplete();
        setIsOpen(false);
        setResult(null);
      }, 2000);
    } catch (err) {
      setResult({
        error: err.response?.data?.error || err.message
      });
    } finally {
      setUploading(false);
    }
  };

  return (
    <>
      {/* Floating Action Menu */}
      <div className="fixed bottom-8 right-8 z-50">
        {/* Options Menu */}
        <AnimatePresence>
          {showOptions && (
            <motion.div
              initial={{ opacity: 0, y: 20, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              exit={{ opacity: 0, y: 20, scale: 0.9 }}
              className="absolute bottom-20 right-0 bg-white rounded-xl shadow-2xl p-3 space-y-2 min-w-[200px] border border-gray-200"
            >
              <button
                onClick={() => {
                  setShowOptions(false);
                  setIsOpen(true);
                }}
                className="w-full flex items-center gap-3 px-4 py-3 rounded-lg hover:bg-blue-50 transition-colors text-left group"
              >
                <div className="bg-blue-100 p-2 rounded-lg group-hover:bg-blue-200 transition-colors">
                  <FileSpreadsheet className="w-5 h-5 text-blue-600" />
                </div>
                <div>
                  <p className="font-semibold text-gray-900 text-sm">Import Excel</p>
                  <p className="text-xs text-gray-500">Bulk upload contacts</p>
                </div>
              </button>

              <div onClick={() => setShowOptions(false)}>
                <QuickAddContact onContactAdded={onComplete} />
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Main FAB Button */}
        <motion.button
          whileHover={{ scale: 1.05, rotate: showOptions ? 45 : 0 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => setShowOptions(!showOptions)}
          animate={{ rotate: showOptions ? 45 : 0 }}
          className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-4 rounded-full shadow-2xl hover:shadow-3xl group"
        >
          <Plus className="w-6 h-6" />
          {!showOptions && (
            <motion.span
              initial={{ opacity: 0, x: 10 }}
              animate={{ opacity: 1, x: 0 }}
              className="absolute right-16 top-1/2 -translate-y-1/2 bg-gray-900 text-white px-3 py-1 rounded-lg text-sm whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity"
            >
              Add Contacts
            </motion.span>
          )}
        </motion.button>
      </div>

      {/* Modal */}
      <AnimatePresence>
        {isOpen && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setIsOpen(false)}
              className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50"
            />
            <motion.div
              initial={{ opacity: 0, scale: 0.95, y: 20 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95, y: 20 }}
              className="fixed inset-0 z-50 flex items-center justify-center p-4"
            >
              <div className="bg-white rounded-2xl shadow-2xl max-w-lg w-full p-8 relative">
                <button
                  onClick={() => setIsOpen(false)}
                  className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 transition-colors"
                >
                  <X className="w-6 h-6" />
                </button>

                <div className="flex items-center gap-3 mb-6">
                  <div className="bg-blue-100 p-3 rounded-xl">
                    <FileSpreadsheet className="w-6 h-6 text-blue-600" />
                  </div>
                  <div className="flex-1">
                    <h2 className="text-2xl font-bold text-gray-900">Import Contacts</h2>
                    <p className="text-sm text-gray-500">Upload Excel file with employee data</p>
                  </div>
                  {imports.length > 0 && (
                    <button
                      onClick={handleAddMore}
                      className="inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
                    >
                      <Plus className="w-5 h-5" />
                      Add More
                    </button>
                  )}
                </div>

                {imports.length > 0 && (
                  <div className="mb-6 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-4 border border-blue-200">
                    <div className="flex items-center justify-between mb-3">
                      <div className="flex items-center gap-2">
                        <FileSpreadsheet className="w-5 h-5 text-blue-600" />
                        <p className="text-sm font-semibold text-gray-800">Imported Excel Sheets ({imports.length})</p>
                      </div>
                    </div>
                    <div className="max-h-64 overflow-auto space-y-2">
                      {imports.map(item => (
                        <div key={item.id} className="flex items-center justify-between bg-white rounded-lg p-3 shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
                          <div className="flex items-center gap-3 flex-1">
                            <div className="bg-green-100 p-2 rounded-lg">
                              <FileSpreadsheet className="w-5 h-5 text-green-600" />
                            </div>
                            <div className="flex-1 min-w-0">
                              <p className="text-sm font-semibold text-gray-900 truncate">{item.name}</p>
                              <div className="flex items-center gap-3 mt-1">
                                <p className="text-xs text-gray-500">{new Date(item.uploadedAt).toLocaleString()}</p>
                                <span className="text-xs bg-green-100 text-green-700 px-2 py-0.5 rounded-full font-medium">+{item.added}</span>
                                <span className="text-xs bg-yellow-100 text-yellow-700 px-2 py-0.5 rounded-full font-medium">~{item.skipped}</span>
                              </div>
                            </div>
                          </div>
                          <button 
                            onClick={() => handleDeleteImport(item.id)} 
                            className="ml-3 p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                            title="Remove from history"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Drag & Drop Area */}
                <div
                  onDragOver={handleDragOver}
                  onDragLeave={handleDragLeave}
                  onDrop={handleDrop}
                  onClick={() => fileInputRef.current?.click()}
                  className={`
                    border-2 border-dashed rounded-xl p-12 text-center cursor-pointer
                    transition-all duration-200
                    ${isDragging 
                      ? 'border-blue-500 bg-blue-50' 
                      : 'border-gray-300 hover:border-blue-400 hover:bg-blue-50/50'
                    }
                  `}
                >
                  <Upload className={`w-12 h-12 mx-auto mb-4 ${isDragging ? 'text-blue-500' : 'text-gray-400'}`} />
                  <p className="text-gray-700 font-medium mb-2">
                    {isDragging ? 'Drop file here' : 'Drag & drop or click to browse'}
                  </p>
                  <p className="text-sm text-gray-500">Excel files (.xlsx, .xls)</p>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={handleFileSelect}
                    className="hidden"
                  />
                </div>

                {/* Upload Progress */}
                {uploading && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mt-4 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg border border-blue-200"
                  >
                    <div className="flex items-center gap-3 mb-2">
                      <div className="animate-spin rounded-full h-6 w-6 border-3 border-blue-600 border-t-transparent"></div>
                      <div className="flex-1">
                        <p className="text-blue-900 font-semibold">Processing Excel file...</p>
                        <p className="text-xs text-blue-600 mt-0.5">This may take a moment for large datasets</p>
                      </div>
                    </div>
                    <div className="w-full bg-blue-200 rounded-full h-2 overflow-hidden">
                      <div className="bg-blue-600 h-full rounded-full animate-pulse" style={{width: '100%'}}></div>
                    </div>
                  </motion.div>
                )}

                {/* Result Messages */}
                {result && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mt-4"
                  >
                    {result.error ? (
                      <div className="p-4 bg-red-50 rounded-lg flex items-start gap-3">
                        <AlertCircle className="w-5 h-5 text-red-600 flex-shrink-0 mt-0.5" />
                        <div>
                          <p className="text-red-900 font-medium">Upload Failed</p>
                          <p className="text-red-700 text-sm mt-1">{result.error}</p>
                        </div>
                      </div>
                    ) : (
                      <div className="p-4 bg-green-50 rounded-lg">
                        <div className="flex items-start gap-3 mb-3">
                          <CheckCircle className="w-5 h-5 text-green-600 flex-shrink-0 mt-0.5" />
                          <div>
                            <p className="text-green-900 font-medium">Import Successful!</p>
                          </div>
                        </div>
                        <div className="grid grid-cols-2 gap-4 text-sm">
                          <div className="bg-white p-3 rounded-lg">
                            <p className="text-gray-600">Added</p>
                            <p className="text-2xl font-bold text-green-600">{result.added || 0}</p>
                          </div>
                          <div className="bg-white p-3 rounded-lg">
                            <p className="text-gray-600">Skipped</p>
                            <p className="text-2xl font-bold text-yellow-600">{result.skipped || 0}</p>
                          </div>
                        </div>
                        {result.errors && result.errors.length > 0 && (
                          <div className="mt-3 p-3 bg-yellow-50 rounded-lg">
                            <p className="text-yellow-900 text-sm font-medium mb-2">Errors:</p>
                            <ul className="text-xs text-yellow-800 space-y-1">
                              {result.errors.map((err, i) => (
                                <li key={i}>• {err}</li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </div>
                    )}
                  </motion.div>
                )}

                {/* Instructions */}
                <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                  <p className="text-sm font-medium text-gray-700 mb-2">Excel Format:</p>
                  <ul className="text-xs text-gray-600 space-y-1">
                    <li>• Column 1: Name <span className="text-red-600 font-semibold">*</span></li>
                    <li>• Column 2: Email <span className="text-red-600 font-semibold">*</span></li>
                    <li>• Column 3: Phone <span className="text-red-600 font-semibold">*</span></li>
                    <li>• Column 4: Company <span className="text-gray-400">(Optional)</span></li>
                    <li>• Column 5: Designation <span className="text-gray-400">(Optional)</span></li>
                  </ul>
                  <p className="text-xs text-gray-500 mt-2 italic">* Required fields</p>
                </div>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </>
  );
}
