import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import dayjs from 'dayjs';

// Auth Pages
import Login from "./pages/Login";
import Register from "./pages/Register";
import AdminUsers from "./pages/AdminUsers";
import Unauthorized from "./pages/Unauthorized";

// Application Components
import ContactList from './components/ContactList';
import API from './utils/api';
import { Users, CheckCircle, FileText, Clock, Bell, Settings } from 'lucide-react';

// Main Application Component (Your original app with stats sidebar)
function MainApplication() {
  const { user, logout } = useAuth();
  const [contacts, setContacts] = useState([]);
  const [requirements, setRequirements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    loadData();
  }, [refreshTrigger]);

  const loadData = async () => {
    try {
      const [contactsRes, requirementsRes] = await Promise.all([
        API.get('/contacts'),
        API.get('/requirements')
      ]);
      setContacts(contactsRes.data);
      setRequirements(requirementsRes.data);
    } catch (err) {
      console.error('Error loading dashboard data:', err);
    } finally {
      setLoading(false);
    }
  };

  const contacted = contacts.filter(c => c.latest_log);
  const hasRequirement = contacts.filter(c => c.latest_log?.response === 'yes');
  
  // Count only pending follow-ups (due today or overdue, and not completed)
  const today = dayjs().startOf('day');
  const pendingFollowups = contacts.filter(c => {
    if (!c.latest_log?.follow_up_date) return false;
    if (c.latest_log?.follow_up_completed) return false;
    const followUpDate = dayjs(c.latest_log.follow_up_date).startOf('day');
    return followUpDate.isBefore(today) || followUpDate.isSame(today);
  });

  const stats = [
    {
      label: 'Total Contacts',
      value: contacts.length,
      icon: Users,
      color: 'text-blue-500',
      bgColor: 'bg-gradient-to-br from-blue-50 to-blue-100',
      iconBg: 'bg-blue-500'
    },
    {
      label: 'Contacted',
      value: contacted.length,
      icon: CheckCircle,
      color: 'text-emerald-500',
      bgColor: 'bg-gradient-to-br from-emerald-50 to-emerald-100',
      iconBg: 'bg-emerald-500'
    },
    {
      label: 'Has Requirements',
      value: hasRequirement.length,
      icon: FileText,
      color: 'text-purple-500',
      bgColor: 'bg-gradient-to-br from-purple-50 to-purple-100',
      iconBg: 'bg-purple-500'
    },
    {
      label: 'Pending Follow-ups',
      value: pendingFollowups.length,
      icon: Clock,
      color: 'text-orange-500',
      bgColor: 'bg-gradient-to-br from-orange-50 to-orange-100',
      iconBg: 'bg-orange-500'
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b px-6 py-3">
        <div className="flex justify-between items-center">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl flex items-center justify-center shadow-md">
              <Users className="w-7 h-7 text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">Referral System</h1>
              <p className="text-xs text-gray-500">Track Contacts. Capture Opportunities.</p>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <button className="p-2 hover:bg-gray-100 rounded-lg transition-colors">
              <Settings className="w-5 h-5 text-gray-600" />
            </button>
            <div className="text-right">
              <div className="text-sm font-medium text-gray-700">User</div>
              <div className="text-xs text-gray-500">{user?.role || 'Recruiter'}</div>
            </div>
            <div className="relative group">
              <button className="w-10 h-10 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center text-white font-semibold hover:shadow-lg transition-shadow">
                {user?.name?.charAt(0).toUpperCase() || 'U'}
              </button>
              <div className="hidden group-hover:block absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-xl py-2 z-50 border border-gray-100">
                <div className="px-4 py-2 border-b">
                  <p className="text-sm font-medium text-gray-900">{user?.name}</p>
                  <p className="text-xs text-gray-500">{user?.email}</p>
                  <p className="text-xs text-blue-600 mt-1 capitalize">{user?.role}</p>
                </div>
                {(user?.role === 'admin' || user?.role === 'superadmin') && (
                  <a href="/admin/users" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                    Manage Users
                  </a>
                )}
                <button
                  onClick={logout}
                  className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100"
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Layout: Sidebar + Content */}
      <div className="flex h-[calc(100vh-73px)]">
        {/* Left Sidebar - Stats */}
        <div className="w-80 bg-white border-r border-gray-200 p-4 overflow-y-auto">
          <div className="space-y-3">
            {/* Dashboard Overview Title */}
            <div className="mb-4">
              <h3 className="text-sm font-medium text-gray-500 flex items-center gap-2">
                <div className="w-1 h-5 bg-gradient-to-b from-blue-400 to-purple-400 rounded-full"></div>
                Dashboard Overview
              </h3>
            </div>

            {/* Stats Cards */}
            {loading ? (
              <div className="flex items-center justify-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-500 border-t-transparent"></div>
              </div>
            ) : (
              stats.map((stat) => {
                const Icon = stat.icon;
                return (
                  <div
                    key={stat.label}
                    className={`${stat.bgColor} rounded-xl p-4 shadow-sm hover:shadow-md transition-all cursor-pointer`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <p className="text-sm font-medium text-gray-600">{stat.label}</p>
                      <div className={`${stat.iconBg} rounded-xl p-2 shadow-sm`}>
                        <Icon className="w-5 h-5 text-white" />
                      </div>
                    </div>
                    <p className={`text-3xl font-bold ${stat.color}`}>{stat.value}</p>
                    <div className="mt-3 h-1.5 bg-white/60 rounded-full overflow-hidden">
                      <div 
                        className={`h-full ${stat.iconBg} rounded-full transition-all duration-500`}
                        style={{ width: `${Math.min((stat.value / Math.max(contacts.length, 1)) * 100, 100)}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>

        {/* Right Content - Contact List */}
        <div className="flex-1 overflow-y-auto p-6">
          <ContactList 
            refreshTrigger={refreshTrigger}
            onDataChange={() => setRefreshTrigger(prev => prev + 1)}
            notificationCount={pendingFollowups.length}
            onNotificationClick={() => setShowNotifications(!showNotifications)}
          />
        </div>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/unauthorized" element={<Unauthorized />} />

          {/* Protected Route - Your Original Application */}
          <Route
            path="/*"
            element={
              <ProtectedRoute>
                <MainApplication />
              </ProtectedRoute>
            }
          />
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  );
}
