import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Bell, Settings, Users } from 'lucide-react';

export default function DashboardHeader({ notificationCount = 0, onNotificationClick }) {
  const handleNotificationClick = () => {
    if (onNotificationClick) onNotificationClick();
  };

  const handleSettingsClick = () => {
    console.log('Settings clicked');
  };

  return (
    <motion.header
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="border-b bg-white/95 backdrop-blur supports-[backdrop-filter]:bg-white/60 sticky top-0 z-40 shadow-sm"
    >
      <div className="flex h-14 items-center justify-between px-8">
        {/* Logo */}
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-400 to-blue-500 rounded-lg flex items-center justify-center shadow-sm">
            <Users className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-lg font-semibold text-blue-600">Referral System</h1>
            <p className="text-xs text-gray-400">Track Contacts. Capture Opportunities.</p>
          </div>
        </div>

        {/* Right Side */}
        <div className="flex items-center gap-3">
          {/* Notifications */}
          <motion.div className="relative" whileHover={{ scale: 1.05 }}>
            <button
              onClick={handleNotificationClick}
              className="relative hover:bg-gray-50 transition-all duration-200 p-2 rounded-lg"
            >
              <Bell className={`w-5 h-5 transition-all duration-200 ${notificationCount > 0 ? 'text-orange-500' : 'text-gray-600'}`} />
              {notificationCount > 0 && (
                <motion.span
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="absolute -top-1 -right-1 w-5 h-5 p-0 flex items-center justify-center text-xs bg-orange-500 text-white rounded-full font-bold shadow-sm"
                >
                  {notificationCount}
                </motion.span>
              )}
            </button>
          </motion.div>

          {/* Settings */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            onClick={handleSettingsClick}
            className="hover:bg-gray-50 transition-all duration-200 p-2 rounded-lg"
          >
            <Settings className="w-5 h-5 text-gray-600" />
          </motion.button>

          {/* User Avatar */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            className="flex items-center gap-2 cursor-pointer hover:bg-gray-50 px-2 py-1.5 rounded-lg transition-all duration-200"
          >
            <div className="hidden sm:block text-right">
              <p className="text-xs font-medium text-gray-900">User</p>
              <p className="text-xs text-gray-400">Recruiter</p>
            </div>
            <div className="w-8 h-8 bg-gradient-to-br from-blue-400 to-blue-500 rounded-full flex items-center justify-center text-white font-semibold text-sm shadow-sm">
              U
            </div>
          </motion.div>
        </div>
      </div>
    </motion.header>
  );
}
