import axios from 'axios';

// Use relative path for API since frontend and backend are in same container
const getApiUrl = () => {
  // In unified container, nginx proxies /api/ to backend
  // Use relative path so it works in any environment
  return '/api';
};

const API = axios.create({ 
  baseURL: getApiUrl(),
  timeout: 60000, // 60 second timeout to handle Azure Container App cold starts
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add request interceptor for debugging
API.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method?.toUpperCase()} request to: ${config.baseURL}${config.url}`);
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for better error handling
API.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error:', {
      message: error.message,
      status: error.response?.status,
      url: error.config?.url,
      baseURL: error.config?.baseURL
    });
    return Promise.reject(error);
  }
);

export default API;
