import React, { useState } from 'react';
import API from '../utils/api';

export default function ContactForm(){
  const [form, setForm] = useState({name:'', email:'', phone:'', company:'', designation:''});
  const [msg, setMsg] = useState(null);

  const submit = async e => {
    e.preventDefault();
    try{
      const res = await API.post('/contacts', form);
      if(res.data.existing){
        setMsg('Contact already exists — opened existing contact.');
      } else setMsg('Contact added');
      setForm({name:'', email:'', phone:'', company:'', designation:''});
    }catch(err){ setMsg('Error: '+ (err.response?.data?.error||err.message)); }
  }

  return (
    <div className="card" style={{marginBottom:12}}>
      <h2>Add Former Employee</h2>
      {msg && <div style={{marginBottom:8}}>{msg}</div>}
      <form onSubmit={submit}>
        <input placeholder="Name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} />
        <input placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} />
        <input placeholder="Phone" value={form.phone} onChange={e=>setForm({...form,phone:e.target.value})} />
        <input placeholder="Company" value={form.company} onChange={e=>setForm({...form,company:e.target.value})} />
        <input placeholder="Designation" value={form.designation} onChange={e=>setForm({...form,designation:e.target.value})} />
        <div style={{marginTop:8}}>
          <button>Add</button>
        </div>
      </form>
    </div>
  );
}
