import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import dayjs from 'dayjs';

export default function Dashboard(){
  const [contacts, setContacts] = useState([]);
  useEffect(()=>{API.get('/contacts').then(r=>setContacts(r.data));},[]);

  const pendingFollowups = contacts.filter(c => c.latest_log?.follow_up_date);

  return (
    <div className="card" style={{marginTop:12}}>
      <h3>Dashboard</h3>
      <div>Total contacts: {contacts.length}</div>
      <div>Pending follow-ups: {pendingFollowups.length}</div>
      <div style={{fontSize:12}}>(Follow-ups show if a follow-up date exists.)</div>
    </div>
  );
}
