import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ContactList from './components/ContactList';
import Dashboard from './components/Dashboard';
import ExcelImport from './components/ExcelImport';
import FollowUps from './components/FollowUps';
import DashboardHeader from './components/DashboardHeader';

export default function App() {
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const [pendingFollowups, setPendingFollowups] = useState(0);
  const [showFollowupsModal, setShowFollowupsModal] = useState(false);

  const handleRefresh = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  const handleFollowupsUpdate = (count) => {
    setPendingFollowups(count);
  };

  const handleNotificationClick = () => {
    setShowFollowupsModal(true);
  };

  const handleCloseFollowupsModal = () => {
    setShowFollowupsModal(false);
  };

  return (
    <div className="min-h-screen pb-16 bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      {/* Header */}
      <DashboardHeader />

      {/* Main Content - Contact list uses full width with no side margins */}
      <main className="w-full px-0 py-4 md:py-6">
        <div className="w-full">
          <div className="w-full">
            <ContactList
              refreshTrigger={refreshTrigger}
              onDataChange={handleRefresh}
              notificationCount={pendingFollowups}
              onNotificationClick={handleNotificationClick}
            />
          </div>
        </div>
      </main>

      {/* Hidden FollowUps component - only renders modal when showModal is true */}
      <FollowUps
        refreshTrigger={refreshTrigger}
        onDataChange={handleRefresh}
        onFollowupsUpdate={handleFollowupsUpdate}
        showModal={showFollowupsModal}
        onCloseModal={handleCloseFollowupsModal}
      />

      {/* Excel Import FAB */}
      <ExcelImport onComplete={handleRefresh} />

      {/* Footer */}
      <motion.footer
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-md border-t border-gray-200 py-2.5 z-30 shadow-sm"
      >
        <div className="w-full px-8">
          <p className="text-center text-xs text-gray-500">
            Built with <span className="text-pink-500">â™¥</span> for efficient staffing operations
          </p>
        </div>
      </motion.footer>
    </div>
  );
}
