import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Users, Search, Phone, Mail, User, Clock, CheckCircle, XCircle, AlertCircle, Bell } from 'lucide-react';
import API from '../utils/api';
import RequirementForm from './RequirementForm';

export default function ContactList({ refreshTrigger, onDataChange, notificationCount = 0, onNotificationClick }) {
  const [contacts, setContacts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [loading, setLoading] = useState(true);
  const didInitRef = useRef(false);

  const load = async (showSpinner) => {
    if (showSpinner) setLoading(true);
    try {
      const res = await API.get('/contacts');
      setContacts(res.data);
    } catch (err) {
      console.error('Error loading contacts:', err);
    } finally {
      if (showSpinner) setLoading(false);
    }
  };

  useEffect(() => {
    if (!didInitRef.current) {
      didInitRef.current = true;
      load(true);
    } else {
      load(false);
    }
  }, [refreshTrigger]);

  const filteredContacts = contacts.filter((c) => {
    const matchesSearch =
      c.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.company?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilter =
      filterStatus === 'all' ||
      (filterStatus === 'contacted' && c.latest_log) ||
      (filterStatus === 'not_contacted' && !c.latest_log) ||
      (filterStatus === 'has_requirement' && c.latest_log?.response === 'yes') ||
      (filterStatus === 'no_requirement' && c.latest_log?.response === 'no');

    return matchesSearch && matchesFilter;
  });

  const getStatusBadge = (contact) => {
    if (!contact.latest_log) {
      return <span className="badge-warning">Not Contacted</span>;
    }
    if (contact.latest_log.response === 'yes') {
      return <span className="badge-success">Has Requirement</span>;
    }
    if (contact.latest_log.response === 'no') {
      return <span className="badge-danger">No Requirement</span>;
    }
    return <span className="badge-info">Pending</span>;
  };

  return (
    <>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white/90 backdrop-blur-sm rounded-xl border border-gray-100 shadow-lg overflow-hidden"
      >
        {/* Header with Gradient */}
        <div className="bg-gradient-to-r from-blue-50 to-indigo-100 p-6 border-b border-blue-100">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <div className="bg-blue-100 backdrop-blur-sm rounded-xl p-2.5">
                <Users className="h-6 w-6 text-blue-700" />
              </div>
              <div>
                <h2 className="text-gray-800 text-2xl font-extrabold tracking-tight">Contact Management</h2>
                <p className="text-gray-600 text-sm">{filteredContacts.length} total contacts</p>
              </div>
            </div>
            <motion.button
              whileHover={{ scale: 1.03 }}
              whileTap={{ scale: 0.98 }}
              onClick={onNotificationClick}
              className="relative flex items-center gap-3 rounded-2xl px-4 py-3 bg-gradient-to-r from-indigo-500 via-purple-500 to-blue-500 text-white shadow-lg shadow-indigo-200 cursor-pointer"
            >
              <span className="relative inline-flex">
                {notificationCount > 0 && (
                  <span className="absolute inset-0 rounded-full bg-white/40 animate-ping"></span>
                )}
                <span className={`relative inline-flex h-11 w-11 items-center justify-center rounded-full bg-white/20 ${notificationCount === 0 ? 'opacity-80' : ''}`}>
                  <Bell className="h-5 w-5" />
                </span>
              </span>
              <span className="text-left">
                <span className="block text-[11px] uppercase tracking-wide text-white/70">Follow-up reminders</span>
                <span className="block text-base font-semibold">
                  {notificationCount > 0 ? `${notificationCount} due today` : 'Nothing due today'}
                </span>
              </span>
              {notificationCount > 0 && (
                <motion.span
                  initial={{ y: -6, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  className="absolute -top-3 -right-3 px-3 py-1 text-xs font-semibold bg-white text-indigo-600 rounded-full shadow-md"
                >
                  Tap to review
                </motion.span>
              )}
            </motion.button>
          </div>

          {/* Search Bar */}
          <div className="relative">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search by name, email, or company..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-2.5 bg-white backdrop-blur-sm border border-blue-200 rounded-xl shadow-sm focus:bg-white focus:outline-none focus:ring-2 focus:ring-blue-300 transition-all"
            />
          </div>
        </div>

        {/* Filter Section */}
        <div className="bg-gradient-to-r from-slate-50 to-blue-50 p-6 border-b border-gray-200">
          <div className="flex items-center gap-2 mb-3">
            <div className="w-1 h-5 bg-gradient-to-b from-indigo-400 to-indigo-500 rounded-full"></div>
            <span className="text-gray-800 text-base font-bold">Filter by Category</span>
          </div>
          <div className="flex gap-2 flex-wrap">
            {['all', 'contacted', 'not_contacted', 'has_requirement', 'no_requirement'].map((status) => (
              <motion.button
                key={status}
                onClick={() => setFilterStatus(status)}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className={`px-5 py-2.5 rounded-xl transition-all duration-200 text-sm font-medium ${
                  filterStatus === status
                    ? 'bg-gradient-to-r from-blue-500 to-indigo-500 text-white shadow-md shadow-blue-200/50 scale-105'
                    : 'bg-white text-gray-600 hover:bg-blue-50 border border-gray-200 hover:border-blue-300 hover:shadow-sm'
                }`}
              >
                {status.replace('_', ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </motion.button>
            ))}
          </div>
        </div>

        {/* Contacts Table Header */}
        <div className="p-6 bg-white border-b border-gray-100">
          <div className="flex items-center gap-2 mb-4">
            <div className="w-1 h-5 bg-gradient-to-b from-blue-400 to-blue-500 rounded-full"></div>
            <span className="text-gray-500 text-sm">
              {filteredContacts.length} Contact{filteredContacts.length !== 1 ? 's' : ''} Found
            </span>
          </div>
        </div>

        {/* Contacts Table */}
        <div className="overflow-hidden">
          {loading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-500 border-t-transparent mx-auto"></div>
              <p className="text-slate-500 mt-4">Loading contacts...</p>
            </div>
          ) : filteredContacts.length === 0 ? (
            <div className="text-center py-12">
              <Users className="w-16 h-16 text-slate-300 mx-auto mb-4" />
              <p className="text-slate-500">No contacts found</p>
            </div>
          ) : (
            <div className="overflow-x-auto min-h-[400px] max-h-[calc(100vh-280px)] overflow-y-auto">
              <table className="w-full">
                <thead className="bg-gradient-to-r from-gray-50/80 to-blue-50/60 sticky top-0 z-10">
                  <tr className="border-b border-gray-200">
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Name</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Email</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Phone</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Status</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Last Contacted</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Contacted By</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Follow-up Date</th>
                    <th className="px-4 py-4 text-left text-sm font-extrabold text-gray-800 tracking-wide">Notes</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredContacts.map((c, index) => {
                    const formatDate = (dateString) => {
                      if (!dateString) return '-';
                      return new Date(dateString).toLocaleDateString('en-US', {
                        month: 'short',
                        day: 'numeric',
                        year: 'numeric'
                      });
                    };

                    const isUpcoming = (dateString) => {
                      if (!dateString) return false;
                      const date = new Date(dateString);
                      const today = new Date();
                      const threeDays = new Date();
                      threeDays.setDate(today.getDate() + 3);
                      return date >= today && date <= threeDays;
                    };

                    return (
                      <tr
                        key={c.id}
                        className={`border-b border-gray-100 hover:bg-gradient-to-r hover:from-blue-50/40 hover:to-indigo-50/30 transition-colors cursor-pointer ${
                          index % 2 === 0 ? 'bg-white' : 'bg-gray-50/30'
                        }`}
                        onClick={() => setSelected(c)}
                      >
                        {/* Name */}
                        <td className="px-4 py-3 text-sm font-medium text-gray-900">{c.name}</td>

                        {/* Email */}
                        <td className="px-4 py-3 text-sm text-gray-600">{c.email || '-'}</td>

                        {/* Phone */}
                        <td className="px-4 py-3 text-sm text-gray-600">{c.phone || '-'}</td>

                        {/* Status */}
                        <td className="px-4 py-3 text-sm">
                          {c.latest_log ? (
                            <span className="inline-flex items-center px-2.5 py-1 rounded-full text-xs font-medium bg-emerald-100 text-emerald-700 border border-emerald-200">
                              <CheckCircle className="w-3 h-3 mr-1" />
                              Contacted
                            </span>
                          ) : (
                            <span className="inline-flex items-center px-2.5 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-600 border border-gray-200">
                              <XCircle className="w-3 h-3 mr-1" />
                              Not Contacted
                            </span>
                          )}
                        </td>

                        {/* Last Contacted */}
                        <td className="px-4 py-3 text-sm text-gray-600">
                          {c.latest_log ? formatDate(c.latest_log.contacted_at) : '-'}
                        </td>

                        {/* Contacted By */}
                        <td className="px-4 py-3 text-sm text-gray-600">
                          {c.latest_log?.contacted_by ? (
                            <span className="font-medium text-blue-600">{c.latest_log.contacted_by}</span>
                          ) : '-'}
                        </td>

                        {/* Follow-up Date */}
                        <td className="px-4 py-3 text-sm">
                          {c.latest_log?.follow_up_date ? (
                            <div className={`flex items-center gap-1.5 px-2.5 py-1 rounded-lg ${
                              isUpcoming(c.latest_log.follow_up_date)
                                ? 'bg-orange-50 border border-orange-200'
                                : 'bg-gray-50'
                            }`}>
                              <Clock className={`w-3.5 h-3.5 ${isUpcoming(c.latest_log.follow_up_date) ? 'text-orange-500' : 'text-gray-400'}`} />
                              <span className={`${isUpcoming(c.latest_log.follow_up_date) ? 'text-orange-600 font-medium' : 'text-gray-600'}`}>
                                {formatDate(c.latest_log.follow_up_date)}
                              </span>
                            </div>
                          ) : '-'}
                        </td>

                        {/* Notes */}
                        <td className="px-4 py-3 text-sm text-gray-600 max-w-xs">
                          {c.latest_log?.notes ? (
                            <div className="truncate italic text-xs bg-blue-50/50 px-2 py-1 rounded" title={c.latest_log.notes}>
                              "{c.latest_log.notes}"
                            </div>
                          ) : '-'}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </motion.div>

      {/* Requirement Form Modal */}
      <AnimatePresence>
        {selected && (
          <RequirementForm
            contact={selected}
            onDone={() => {
              setSelected(null);
              load();
              if (onDataChange) onDataChange();
            }}
          />
        )}
      </AnimatePresence>
    </>
  );
}
