import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Users, Search, Phone, Mail, User, Clock, CheckCircle, XCircle, AlertCircle } from 'lucide-react';
import API from '../utils/api';
import RequirementForm from './RequirementForm';

export default function ContactList({ refreshTrigger, onDataChange }) {
  const [contacts, setContacts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [loading, setLoading] = useState(true);
  const didInitRef = useRef(false);

  const load = async (showSpinner) => {
    if (showSpinner) setLoading(true);
    try {
      const res = await API.get('/contacts');
      setContacts(res.data);
    } catch (err) {
      console.error('Error loading contacts:', err);
    } finally {
      if (showSpinner) setLoading(false);
    }
  };

  useEffect(() => {
    if (!didInitRef.current) {
      didInitRef.current = true;
      load(true);
    } else {
      load(false);
    }
  }, [refreshTrigger]);

  const filteredContacts = contacts.filter((c) => {
    const matchesSearch =
      c.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      c.company?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilter =
      filterStatus === 'all' ||
      (filterStatus === 'contacted' && c.latest_log) ||
      (filterStatus === 'not_contacted' && !c.latest_log) ||
      (filterStatus === 'has_requirement' && c.latest_log?.response === 'yes') ||
      (filterStatus === 'no_requirement' && c.latest_log?.response === 'no');

    return matchesSearch && matchesFilter;
  });

  const getStatusBadge = (contact) => {
    if (!contact.latest_log) {
      return <span className="badge-warning">Not Contacted</span>;
    }
    if (contact.latest_log.response === 'yes') {
      return <span className="badge-success">Has Requirement</span>;
    }
    if (contact.latest_log.response === 'no') {
      return <span className="badge-danger">No Requirement</span>;
    }
    return <span className="badge-info">Pending</span>;
  };

  return (
    <>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white/90 backdrop-blur-sm rounded-xl border border-gray-100 shadow-lg overflow-hidden"
      >
        {/* Header with Gradient */}
        <div className="bg-gradient-to-r from-blue-400 to-purple-400 p-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <div className="bg-white/30 backdrop-blur-sm rounded-xl p-2.5">
                <Users className="h-6 w-6 text-white" />
              </div>
              <div>
                <h2 className="text-white text-lg font-bold">Contact Management</h2>
                <p className="text-white/80 text-sm">{filteredContacts.length} total contacts</p>
              </div>
            </div>
          </div>

          {/* Search Bar */}
          <div className="relative">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search by name, email, or company..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-2.5 bg-white/95 backdrop-blur-sm border border-white/30 rounded-xl shadow-sm focus:bg-white focus:outline-none focus:ring-2 focus:ring-white/50 transition-all"
            />
          </div>
        </div>

        {/* Filter Section */}
        <div className="bg-gradient-to-r from-gray-50/80 to-blue-50/50 p-6 border-b border-gray-100">
          <div className="flex items-center gap-2 mb-3">
            <div className="w-1 h-5 bg-gradient-to-b from-purple-400 to-purple-500 rounded-full"></div>
            <span className="text-gray-500 text-sm font-medium">Filter by Category</span>
          </div>
          <div className="flex gap-2 flex-wrap">
            {['all', 'contacted', 'not_contacted', 'has_requirement', 'no_requirement'].map((status) => (
              <motion.button
                key={status}
                onClick={() => setFilterStatus(status)}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className={`px-5 py-2.5 rounded-xl transition-all duration-200 text-sm font-medium ${
                  filterStatus === status
                    ? 'bg-gradient-to-r from-blue-400 to-purple-400 text-white shadow-md shadow-blue-400/30 scale-105'
                    : 'bg-white text-gray-500 hover:bg-gray-50 border border-gray-100 hover:border-blue-200 hover:shadow-sm'
                }`}
              >
                {status.replace('_', ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </motion.button>
            ))}
          </div>
        </div>

        {/* Contacts Table Header */}
        <div className="p-6 bg-white border-b border-gray-100">
          <div className="flex items-center gap-2 mb-4">
            <div className="w-1 h-5 bg-gradient-to-b from-blue-400 to-blue-500 rounded-full"></div>
            <span className="text-gray-500 text-sm">
              {filteredContacts.length} Contact{filteredContacts.length !== 1 ? 's' : ''} Found
            </span>
          </div>
        </div>

        {/* Contacts List */}
        <div className="p-6 space-y-3 max-h-[65vh] overflow-y-auto bg-white">
          {loading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-500 border-t-transparent mx-auto"></div>
              <p className="text-slate-500 mt-4">Loading contacts...</p>
            </div>
          ) : filteredContacts.length === 0 ? (
            <div className="text-center py-12">
              <Users className="w-16 h-16 text-slate-300 mx-auto mb-4" />
              <p className="text-slate-500">No contacts found</p>
            </div>
          ) : (
            filteredContacts.map((c, index) => (
              <motion.div
                key={c.id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                whileHover={{ scale: 1.02, y: -2 }}
                className="bg-gradient-to-r from-blue-50/60 to-cyan-50/40 border border-blue-100 rounded-xl p-4 hover:shadow-md hover:border-blue-200 transition-all cursor-pointer"
                onClick={() => setSelected(c)}
              >
                <div className="flex justify-between items-start gap-4">
                  <div className="flex-1 space-y-2">
                    {/* Name with Person Icon */}
                    <div className="flex items-center gap-2">
                      <div className="w-8 h-8 bg-gradient-to-br from-blue-400 to-purple-500 rounded-lg flex items-center justify-center shadow-sm">
                        <User className="w-4 h-4 text-white" />
                      </div>
                      <span className="font-semibold text-slate-900">{c.name}</span>
                    </div>

                    {/* Contact Details */}
                    <div className="space-y-1 text-xs text-gray-600">
                      {c.phone && (
                        <div className="flex items-center gap-2">
                          <Phone className="w-3 h-3 text-blue-500" />
                          {c.phone}
                        </div>
                      )}
                      {c.email && (
                        <div className="flex items-center gap-2">
                          <Mail className="w-3 h-3 text-blue-500" />
                          {c.email}
                        </div>
                      )}
                    </div>

                    {/* Last Contact Info */}
                    {c.latest_log && (
                      <div className="mt-2 pt-2 border-t border-blue-100">
                        <div className="flex items-center gap-2 text-xs text-gray-500">
                          <Clock className="w-3 h-3" />
                          Last contacted: {new Date(c.latest_log.contacted_at).toLocaleDateString()}
                        </div>
                        {c.latest_log.contacted_by && (
                          <div className="flex items-center gap-1 text-xs text-gray-600 mt-1">
                            <span>by</span>
                            <span className="font-medium text-blue-600">{c.latest_log.contacted_by}</span>
                          </div>
                        )}
                        {c.latest_log.notes && (
                          <p className="text-xs text-gray-500 mt-1 italic bg-white/70 p-2 rounded">"{c.latest_log.notes}"</p>
                        )}
                      </div>
                    )}
                  </div>

                  {/* Status Badge */}
                  <div className="flex-shrink-0">
                    {getStatusBadge(c)}
                  </div>
                </div>
              </motion.div>
            ))
          )}
        </div>
      </motion.div>

      {/* Requirement Form Modal */}
      <AnimatePresence>
        {selected && (
          <RequirementForm
            contact={selected}
            onDone={() => {
              setSelected(null);
              load();
              if (onDataChange) onDataChange();
            }}
          />
        )}
      </AnimatePresence>
    </>
  );
}
