import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Sparkles, Phone } from 'lucide-react';
import ContactForm from './components/ContactForm';
import ContactList from './components/ContactList';
import Dashboard from './components/Dashboard';
import ExcelImport from './components/ExcelImport';

export default function App() {
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  const handleRefresh = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <div className="min-h-screen pb-20">
      {/* Header */}
      <motion.header
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-gradient-to-r from-blue-600 via-indigo-600 to-purple-600 text-white sticky top-0 z-40 shadow-2xl"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <motion.div
                animate={{ rotate: [0, 360] }}
                transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
                className="bg-white/20 p-3 rounded-2xl backdrop-blur-sm"
              >
                <Phone className="w-8 h-8" />
              </motion.div>
              <div>
                <h1 className="text-3xl font-bold tracking-tight flex items-center gap-2">
                  Contact & Job Tracker
                  <Sparkles className="w-6 h-6 text-yellow-300" />
                </h1>
                <p className="text-blue-100 text-sm mt-1">
                  Streamline your recruitment process with intelligent contact tracking
                </p>
              </div>
            </div>
          </div>
        </div>
      </motion.header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Column - Form and Dashboard */}
          <div className="lg:col-span-1 space-y-6">
            <ContactForm onContactAdded={handleRefresh} />
            <Dashboard refreshTrigger={refreshTrigger} />
          </div>

          {/* Right Column - Contact List */}
          <div className="lg:col-span-2">
            <ContactList refreshTrigger={refreshTrigger} />
          </div>
        </div>
      </main>

      {/* Excel Import FAB */}
      <ExcelImport onComplete={handleRefresh} />

      {/* Footer */}
      <motion.footer
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="fixed bottom-0 left-0 right-0 bg-white/80 backdrop-blur-sm border-t border-gray-200 py-3 z-30"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <p className="text-center text-sm text-gray-600">
            Built with <span className="text-red-500">â™¥</span> for efficient staffing operations
          </p>
        </div>
      </motion.footer>
    </div>
  );
}
