import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import RequirementForm from './RequirementForm';

export default function ContactList(){
  const [contacts, setContacts] = useState([]);
  const [selected, setSelected] = useState(null);

  const load = async ()=>{ const res = await API.get('/contacts'); setContacts(res.data); }
  useEffect(()=>{ load(); },[]);

  return (
    <div className="card">
      <h2>Contacts</h2>
      <div style={{maxHeight: '60vh', overflow:'auto'}}>
        {contacts.map(c=> (
          <div key={c.id} style={{borderBottom:'1px solid #eee', padding:8}}>
            <div style={{display:'flex', justifyContent:'space-between'}}>
              <div>
                <div style={{fontWeight:600}}>{c.name} <span style={{fontSize:12}}>({c.company})</span></div>
                <div style={{fontSize:13}}>{c.email} â€¢ {c.phone}</div>
                <div style={{fontSize:12, marginTop:6}}>Latest: {c.latest_log?.response || 'Not contacted'}</div>
              </div>
              <div>
                <button onClick={()=>setSelected(c)} style={{marginLeft:8}}>Contact / Log</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      {selected && <RequirementForm contact={selected} onDone={()=>{ setSelected(null); load(); }} />}
    </div>
  );
}
