import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Sparkles, Phone } from 'lucide-react';
import ContactList from './components/ContactList';
import Dashboard from './components/Dashboard';
import ExcelImport from './components/ExcelImport';
import FollowUps from './components/FollowUps';
import DashboardHeader from './components/DashboardHeader';

export default function App() {
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleRefresh = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <div className="min-h-screen pb-20 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* New Header with Search and Notifications */}
      <DashboardHeader 
        onMenuToggle={() => setMobileMenuOpen(!mobileMenuOpen)}
        notificationCount={0}
      />

      {/* Brand Header */}
      <motion.div
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-white/90 backdrop-blur-sm border-b border-purple-100 sticky top-16 z-30"
      >
        <div className="w-full px-6 py-3">
          <div className="flex items-center gap-3">
            <motion.div
              animate={{ rotate: [0, 360] }}
              transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
              className="bg-gradient-to-br from-purple-500 to-blue-500 p-2 rounded-lg shadow-md"
            >
              <Phone className="w-5 h-5 text-white" />
            </motion.div>
            <div>
              <h1 className="text-xl font-bold tracking-tight flex items-center gap-2 bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">
                RecruitConnect
                <Sparkles className="w-4 h-4 text-purple-500" />
              </h1>
              <p className="text-purple-600 text-xs font-medium">
                Track Contacts. Capture Opportunities.
              </p>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <main className="w-full px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6 max-w-7xl mx-auto">
          {/* Left Column - Dashboard & Follow-ups (Stats Panel) */}
          <div className="lg:col-span-3 space-y-6">
            <Dashboard refreshTrigger={refreshTrigger} />
          </div>

          {/* Right Column - Contact List & Follow-ups */}
          <div className="lg:col-span-9 space-y-6">
            <FollowUps refreshTrigger={refreshTrigger} onDataChange={handleRefresh} />
            <ContactList refreshTrigger={refreshTrigger} onDataChange={handleRefresh} />
          </div>
        </div>
      </main>

      {/* Excel Import FAB */}
      <ExcelImport onComplete={handleRefresh} />

      {/* Footer */}
      <motion.footer
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-md border-t border-purple-100 py-3 z-30 shadow-lg"
      >
        <div className="w-full px-6">
          <p className="text-center text-sm text-purple-600 font-medium">
            Built with <span className="text-pink-500">â™¥</span> for efficient staffing operations
          </p>
        </div>
      </motion.footer>
    </div>
  );
}
