import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Search, Bell, Settings, Menu, BookOpen } from 'lucide-react';

export default function DashboardHeader({ onMenuToggle, notificationCount = 0 }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [showNotificationDropdown, setShowNotificationDropdown] = useState(false);

  const handleNotificationClick = () => {
    console.log('Notifications clicked');
    setShowNotificationDropdown(!showNotificationDropdown);
  };

  const handleSettingsClick = () => {
    console.log('Settings clicked');
  };

  return (
    <motion.header
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="border-b bg-white/95 backdrop-blur supports-[backdrop-filter]:bg-white/60 sticky top-0 z-40 shadow-sm"
    >
      <div className="flex h-16 items-center gap-4 px-6">
        {/* Mobile Menu Button */}
        <button
          onClick={onMenuToggle}
          className="md:hidden hover:bg-purple-50 transition-all duration-200 hover:scale-110 p-2 rounded-lg"
        >
          <Menu className="h-5 w-5" />
        </button>

        {/* Logo */}
        <div className="flex items-center gap-2">
          <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-600 rounded-lg flex items-center justify-center shadow-lg">
            <BookOpen className="w-5 h-5 text-white" />
          </div>
          <span className="font-semibold hidden sm:inline bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">
            RecruitConnect
          </span>
        </div>

        {/* Search */}
        <div className="flex-1 max-w-md">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <input
              type="text"
              placeholder="Search contacts..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-gray-50 focus:bg-white transition-all duration-200 focus:ring-2 focus:ring-purple-300 border border-purple-100 rounded-lg text-sm outline-none"
            />
          </div>
        </div>

        {/* Right Side */}
        <div className="flex items-center gap-2">
          {/* Notifications */}
          <motion.div className="relative" whileHover={{ scale: 1.1 }}>
            <button
              onClick={handleNotificationClick}
              className="relative hover:bg-purple-50 transition-all duration-200 p-2 rounded-lg"
            >
              <Bell className={`w-5 h-5 transition-all duration-200 ${notificationCount > 0 ? 'animate-pulse text-purple-600' : 'text-gray-700'}`} />
              {notificationCount > 0 && (
                <motion.span
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="absolute -top-1 -right-1 w-5 h-5 p-0 flex items-center justify-center text-xs bg-gradient-to-r from-red-400 to-pink-500 border-0 text-white animate-bounce rounded-full font-bold shadow-md"
                >
                  {notificationCount}
                </motion.span>
              )}
            </button>

            {/* Notification Dropdown */}
            {showNotificationDropdown && notificationCount > 0 && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                className="absolute right-0 mt-2 w-64 bg-white rounded-xl shadow-lg border border-purple-100 z-50"
              >
                <div className="p-4 border-b border-purple-100 bg-gradient-to-r from-purple-50 to-blue-50">
                  <p className="text-sm font-semibold text-gray-900">Notifications</p>
                  <p className="text-xs text-purple-600">You have {notificationCount} pending action{notificationCount > 1 ? 's' : ''}</p>
                </div>
                <div className="p-4 max-h-48 overflow-y-auto space-y-2">
                  <div className="text-sm text-gray-600 text-center py-4">
                    Check the Follow-ups panel for details
                  </div>
                </div>
              </motion.div>
            )}
          </motion.div>

          {/* Settings */}
          <motion.button
            whileHover={{ scale: 1.1, rotate: 12 }}
            onClick={handleSettingsClick}
            className="hover:bg-purple-50 transition-all duration-200 p-2 rounded-lg"
          >
            <Settings className="w-5 h-5 text-gray-700" />
          </motion.button>

          {/* User Avatar */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            className="flex items-center gap-3 cursor-pointer hover:bg-purple-50 p-2 rounded-lg transition-all duration-200"
          >
            <div className="hidden sm:block text-right">
              <p className="text-sm font-medium text-gray-900">User</p>
              <p className="text-xs text-gray-500">Recruiter ‚≠ê</p>
            </div>
            <div className="w-8 h-8 bg-gradient-to-br from-purple-400 to-blue-500 rounded-full flex items-center justify-center text-white font-semibold text-sm ring-2 ring-purple-200 hover:ring-purple-300 transition-all duration-200 shadow-md">
              U
            </div>
          </motion.div>
        </div>
      </div>
    </motion.header>
  );
}
