import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Bell, Phone, Mail, CheckCircle, X } from 'lucide-react';
import API from '../utils/api';
import dayjs from 'dayjs';

export default function FollowUps({ refreshTrigger, onDataChange, onFollowupsUpdate, showModal, onCloseModal }) {
  const [followups, setFollowups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showNotification, setShowNotification] = useState(false);
  const [dueToday, setDueToday] = useState([]);
  const [completing, setCompleting] = useState(null);
  const [dismissedForToday, setDismissedForToday] = useState(false);
  const hasAutoShownRef = useRef(false);

  // Show modal when requested from parent
  useEffect(() => {
    setShowNotification(showModal);
  }, [showModal]);

  const loadFollowups = async (mode = 'auto', showSpinner = false) => {
    if (showSpinner) setLoading(true);
    try {
      const [allRes, pendingRes] = await Promise.all([
        API.get('/contacts/followups/all'),
        API.get('/contacts/followups/pending')
      ]);

      setFollowups(allRes.data);
      setDueToday(pendingRes.data);

      // Update parent with pending count
      if (onFollowupsUpdate) {
        onFollowupsUpdate(pendingRes.data.length);
      }

      // Disabled auto-popup - user must click notification bell to see follow-ups
      // if (
      //   mode === 'auto' &&
      //   pendingRes.data.length > 0 &&
      //   !dismissedForToday &&
      //   !hasAutoShownRef.current
      // ) {
      //   setShowNotification(true);
      //   hasAutoShownRef.current = true;
      // }
    } catch (err) {
      console.error('Error loading follow-ups:', err);
    } finally {
      if (showSpinner) setLoading(false);
    }
  };

  // Initialize dismissal state from localStorage on mount
  useEffect(() => {
    const key = 'followups_notice_dismissed_date';
    const todayStr = dayjs().format('YYYY-MM-DD');
    const stored = typeof window !== 'undefined' ? localStorage.getItem(key) : null;
    setDismissedForToday(stored === todayStr);
  }, []);

  // Load followups when component mounts and when refreshTrigger changes.
  // On mount and interval: use 'auto' to allow a single automatic open.
  // On refreshTrigger updates (e.g., after saving): use 'silent' to avoid re-opening.
  useEffect(() => {
    loadFollowups('auto', true);

    // Check for new follow-ups every 5 minutes (auto mode) - set once on mount
    const interval = setInterval(() => loadFollowups('auto', false), 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (refreshTrigger !== undefined) {
      loadFollowups('silent', false);
    }
  }, [refreshTrigger]);

  const getUrgency = (date) => {
    const today = dayjs().startOf('day');
    const followUpDate = dayjs(date).startOf('day');

    if (followUpDate.isBefore(today)) {
      return 'high'; // Overdue
    } else if (followUpDate.isSame(today)) {
      return 'high'; // Due today
    } else {
      const daysUntil = followUpDate.diff(today, 'day');
      if (daysUntil <= 3) return 'medium';
      return 'low';
    }
  };

  const getUrgencyStyles = (urgency) => {
    switch (urgency) {
      case 'high':
        return 'bg-gradient-to-r from-orange-50/70 to-red-50/50 border-orange-200';
      case 'medium':
        return 'bg-gradient-to-r from-yellow-50/70 to-amber-50/50 border-yellow-200';
      default:
        return 'bg-gradient-to-r from-blue-50/70 to-cyan-50/50 border-blue-200';
    }
  };

  const getUrgencyBadgeStyles = (urgency) => {
    switch (urgency) {
      case 'high':
        return 'bg-orange-100/80 text-orange-700 border-orange-300';
      case 'medium':
        return 'bg-yellow-100/80 text-yellow-700 border-yellow-300';
      default:
        return 'bg-blue-100/80 text-blue-700 border-blue-300';
    }
  };

  const getUrgencyIconColor = (urgency) => {
    switch (urgency) {
      case 'high':
        return 'text-orange-500';
      case 'medium':
        return 'text-yellow-500';
      default:
        return 'text-blue-500';
    }
  };

  const handleComplete = async (logId) => {
    setCompleting(logId);
    try {
      await API.patch(`/contacts/followups/${logId}/complete`);

      // Reload follow-ups from server to ensure consistency (silent to avoid popup)
      await loadFollowups('silent', false);

      // Notify parent to refresh dashboard
      if (onDataChange) {
        onDataChange();
      }
    } catch (err) {
      console.error('Error completing follow-up:', err);
      alert('Failed to mark follow-up as complete');
    } finally {
      setCompleting(null);
    }
  };

  const closeNotificationForToday = () => {
    setShowNotification(false);
    if (onCloseModal) onCloseModal();
    const key = 'followups_notice_dismissed_date';
    const todayStr = dayjs().format('YYYY-MM-DD');
    try {
      if (typeof window !== 'undefined') {
        localStorage.setItem(key, todayStr);
      }
    } catch (_) {}
    setDismissedForToday(true);
  };

  // Only return the modal popup, no main section needed
  return (
    <>
      {/* Notification Drawer for Follow-ups */}
      <AnimatePresence>
        {showNotification && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.4 }}
              exit={{ opacity: 0 }}
              onClick={closeNotificationForToday}
              className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-40"
            />
            <motion.aside
              initial={{ x: '100%' }}
              animate={{ x: 0 }}
              exit={{ x: '100%' }}
              transition={{ type: 'spring', stiffness: 260, damping: 30 }}
              className="fixed inset-y-0 right-0 z-50 w-full max-w-md bg-white shadow-[0_15px_60px_rgba(15,23,42,0.3)] border-l border-gray-100 flex flex-col"
            >
              <div className="bg-gradient-to-br from-indigo-500 via-purple-500 to-blue-500 p-6 text-white">
                <div className="flex items-start justify-between">
                  <div>
                    <div className="flex items-center gap-3">
                      <div className="bg-white/20 p-2 rounded-xl">
                        <Bell className="w-5 h-5" />
                      </div>
                      <div>
                        <p className="text-sm text-white/70">Follow-up Center</p>
                        <h3 className="text-2xl font-semibold">{dueToday.length} pending reminder{dueToday.length === 1 ? '' : 's'}</h3>
                      </div>
                    </div>
                  </div>
                  <button
                    onClick={closeNotificationForToday}
                    className="text-white/80 hover:text-white rounded-full p-2 transition-colors"
                  >
                    <X className="w-5 h-5" />
                  </button>
                </div>
              </div>

              <div className="flex-1 overflow-y-auto px-6 py-5 space-y-4 bg-gradient-to-b from-white via-white to-slate-50">
                {dueToday.length === 0 && (
                  <div className="text-center text-gray-500 py-12 border border-dashed border-gray-200 rounded-2xl bg-white/70">
                    <p className="text-lg font-semibold text-slate-700">You're all caught up!</p>
                    <p className="text-sm text-slate-500 mt-1">No pending follow-ups for today.</p>
                  </div>
                )}

                {dueToday.map((followup) => (
                  <motion.div
                    key={followup.id}
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.25 }}
                    className="bg-white border border-slate-100 rounded-2xl p-4 shadow-sm hover:shadow-md transition-all duration-200"
                  >
                    <div className="flex items-start justify-between gap-3">
                      <div className="flex-1">
                        <p className="text-sm text-slate-500">Contact</p>
                        <h4 className="text-lg font-semibold text-slate-900">{followup.name}</h4>
                        <div className="mt-3 space-y-2 text-sm text-slate-600">
                          {followup.phone && (
                            <div className="flex items-center gap-2">
                              <Phone className="w-4 h-4 text-blue-500" />
                              {followup.phone}
                            </div>
                          )}
                          {followup.email && (
                            <div className="flex items-center gap-2">
                              <Mail className="w-4 h-4 text-purple-500" />
                              {followup.email}
                            </div>
                          )}
                        </div>
                        {followup.notes && (
                          <p className="text-sm text-slate-500 mt-3 italic bg-slate-50 border border-slate-100 p-3 rounded-xl">
                            “{followup.notes}”
                          </p>
                        )}
                      </div>
                      <div className="flex flex-col items-end gap-3">
                        <span className={`text-xs font-semibold px-3 py-1 rounded-full shadow-sm ${
                          dayjs(followup.follow_up_date).isBefore(dayjs(), 'day')
                            ? 'bg-orange-100 text-orange-700'
                            : 'bg-emerald-100 text-emerald-700'
                        }`}>
                          {dayjs(followup.follow_up_date).isBefore(dayjs(), 'day') ? 'Overdue' : 'Due today'}
                        </span>
                        <button
                          onClick={() => handleComplete(followup.id)}
                          disabled={completing === followup.id}
                          className="inline-flex items-center gap-2 bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 text-white px-4 py-2 rounded-xl text-sm font-medium transition-all disabled:opacity-50"
                        >
                          {completing === followup.id ? (
                            <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent"></div>
                          ) : (
                            <CheckCircle className="w-4 h-4" />
                          )}
                          Mark done
                        </button>
                      </div>
                    </div>
                  </motion.div>
                ))}
              </div>

              <div className="border-t border-slate-100 p-5 bg-white">
                <button
                  onClick={closeNotificationForToday}
                  className="w-full text-slate-600 hover:text-slate-900 font-medium py-2.5 rounded-xl hover:bg-slate-100 transition-colors"
                >
                  Close drawer
                </button>
              </div>
            </motion.aside>
          </>
        )}
      </AnimatePresence>
    </>
  );
}
